--USE MASTER--CREATE DATABASE Studentss ON (Name=Studentss, FILENAME = 'C:\temp\Studentss.mdf' )CREATE TABLE People(PersonID INT IDENTITY PRIMARY KEY, LastName VARCHAR(50) NOT NULL, FirstName VARCHAR(50) NOT NULL, SubjectName VARCHAR(80), Grade FLOAT)CREATE TABLE Subjects (ID_Subject INT PRIMARY KEY , SubjectName VARCHAR (80))
CREATE TABLE Grade (Id INT FOREIGN KEY REFERENCES People ON DELETE CASCADE, Id_Subject INT FOREIGN KEY REFERENCES Subjects ON DELETE CASCADE, grade FLOAT, CONSTRAINT PK_Grade PRIMARY KEY (Id,Id_Subject))-- What is the optimal structure of such a database?-- The optimal structure is one with connected tables which hold a certain amount of information as the tables above-- in which we have 3 tables in the database, one with personal data mentioning generally grades and subject and a description of grades-- and in the other two tables which must be connected to the original table, there are more details on Subjects and Grades with separate remarksINSERT INTO People (LastName,FirstName, SubjectName, Grade) VALUES ( 'Lovell', 'Dominic', 'MDD', 4.9)INSERT INTO People (LastName,FirstName, SubjectName, Grade) VALUES ( 'Kowalksi', 'Jan', 'MDD', 3.0)INSERT INTO People (LastName,FirstName, SubjectName, Grade) VALUES ( 'Smith', 'John', 'MDD', 3.9)INSERT INTO People (LastName,FirstName, SubjectName, Grade) VALUES ( 'Johnson', 'Johnson', 'MDD', 4.2)INSERT INTO People (LastName,FirstName, SubjectName, Grade) VALUES ( 'Johnson', 'Graham', 'MDD', 3.9)INSERT INTO Subjects (ID_Subject,SubjectName) VALUES (1,'MDD')INSERT INTO Grade (Id, Id_Subject, grade) VALUES (1,1, 5.0)ALTER TABLE People ADD GradesDescription VARCHAR(1000)ALTER TABLE Grade ADD GradeRemarks VARCHAR(1000)ALTER TABLE People ADD StartDate DATETIMESELECT * FROM GradeSELECT * FROM People-- ALTER TABLE <t a b l e name> ALTER COLUMN <column name> <n e w d a t a type>ALTER TABLE People ALTER COLUMN Grade INTINSERT INTO People (LastName,FirstName, SubjectName, Grade, GradesDescription, StartDate) VALUES ( 'Johnson', 'Graham', 'MDD', 4, 'Very good',01/10/2018)INSERT INTO Subjects (ID_Subject,SubjectName) VALUES (2,'MDD')INSERT INTO Grade (Id, Id_Subject, grade) VALUES (2,2, 5.0)INSERT INTO Grade (Id, Id_Subject, grade, GradeRemarks) VALUES (3,3, 5.0, 'Very good')ALTER TABLE People DROP COLUMN GradesDescr